    val turnBlockNames = mutableListOf<String>()
    var teamNum = 0
    var currentTurn = 0

    fun updateCurrentTurn(){
        currentTurn++
        if (currentTurn == teamNum){
            currentTurn = 0
        }
    }

    fun deleteNamesInTurn(names: List<String>){
        for(i in names){
            guessNames.remove(i)
            turnBlockNames.add(i)
        }
    }

    fun resetNamesAfterTurn(){
        for(i in turnBlockNames){
            guessNames.add(i)
        }
    }

    fun getGuessListNames(): List<String>{
        guessNames.shuffle()
        if (guessNames.size >= 5){
            return guessNames.subList(0, 5).toList()
        }
        else{
            MainActivity.UpdateMainMenuRcl.endGame()
            return listOf("end","end","end","end","end")
        }
    }

    fun deleteName(name: String){
        guessNames.remove(name)
    }
